def find_min_distance(s, char1, char2):
    positions = []
    for i, c in enumerate(s):
        if c == char1:
            positions.append(i)
        elif c == char2:
            positions.append(i)

    if not positions or len(positions) < 2:
        return -1  # Characters not found or less than 2 occurrences

    min_distance = abs(positions[0] - positions[1])
    for i in range(1, len(positions) - 1):
        current_distance = abs(positions[i] - positions[i + 1])
        if current_distance < min_distance:
            min_distance = current_distance

    return min_distance

