def longest_continuous_increasing_subarray(arr):
    if not arr:
        return []

    max_length = 1
    start = 0
    max_start = 0
    max_end = 0

    for i in range(1, len(arr)):
        if arr[i] > arr[i - 1]:
            if i - start + 1 > max_length:
                max_length = i - start + 1
                max_start = start
                max_end = i
        else:
            start = i

    return arr[max_start:max_end + 1]

def quick_sort(arr):
    if len(arr) <= 1:
        return arr
    else:
        pivot = arr[len(arr) // 2]
        left = [x for x in arr if x < pivot]
        middle = [x for x in arr if x == pivot]
        right = [x for x in arr if x > pivot]
        return left + middle + right

