def create_partition(arr):
    n = len(arr)
    sum_arr = sum(arr)

    if sum_arr % 2 != 0:
        return False, [], []

    dp = [[False for i in range(sum_arr // 2 + 1)] for j in range(n + 1)]

    for i in range(n + 1):
        for j in range(1, sum_arr // 2 + 1):
            if arr[i - 1] > j:
                dp[i][j] = dp[i - 1][j]
            else:
                dp[i][j] = dp[i - 1][j] or dp[i - 1][j - 1]

