def sieve_of_eratosthenes(limit):
    primes = []
    mark = [True for _ in range(limit + 1)]
    p = 2
    while p * p <= limit:
        if mark[p]:
            for i in range(p * p, limit + 1, p):
                mark[i] = False
        p += 1
    for p in range(2, limit):
        if mark[p]:
            primes.append(p)
            if len(primes) == 10:
                break
    return primes

