function countCharacters(str) {
    // Initialize an empty object to store the character counts
    const charCount = {};

    // Iterate through each character in the string
    for (let char of str) {
        // Check if the character already exists in the object
        if (charCount[char]) {
            // If it does, increment its count
            charCount[char]++;
        } else {
            // If it doesn't, add it to the object with a count of 1
            charCount[char] = 1;
        }
    }

    // Return the object with character counts
    return charCount;
}

