def find_max_sum(arr):
    # Create a dictionary to store the frequency of each element
    freq = {}
    for num in arr:
        if num in freq:
            freq[num] += 1
        else:
            freq[num] = 1
    # Find the element with the highest frequency
    max_freq = 0
    max_measure = None
    for num, count in freq.items():
        if count > max_freq:
            max_freq = count
            max_measure = num
    return max_measure

