def min_distance(s, char1, char2):
    positions = []
    for i, c in enumerate(s):
        if c == char1:
            positions.append(i)
        elif c == char2:
            positions.append(i)

    if not positions:
        return -1  # Characters not found in the string

    min_dist = len(s)
    for i in range(len(positions) - 1):
        dist = abs(positions[i] - positions[i + 1])
        if dist < min_dist:
            min_dist = dist

    return min_dist

