#include <vector>

bool isSelfNumber(int number) {
    std::vector<bool> cache(10); // Cache to store if a number is a self number
    cache[0] = true; // 0 is a self number

    for (int i = 1; i < number; ++i) {
        int sum = 0;
        while (sum < i) {
            sum += cache[i - sum];
        }
        cache[i] = (sum == i);
    }

    return cache[number];
}

