def min_distance(s, char1, char2):
    positions = []
    for i, c in enumerate(s):
        if c == char1:
            positions.append(i)
        elif c == char2:
            positions.append(i)

    if not positions:
        return -1

    min_distance = len(s)
    for i in range(len(positions)-1):
        distance = abs(positions[i] - positions[i+1])
        if distance < min_distance:
            min_distance = distance

    return min_distance

